mapper.select:
SELECT id, zone_id, price, status, online_user_id 
FROM machines 
WHERE id = #{id}

SELECT id, zone_id, price, status, online_user_id 
FROM machines 
WHERE zone_id = #{zoneId}

mapper.update:
UPDATE machines 
SET zone_id = #{zoneId},
    price = #{price},
    status = #{status},
    online_user_id = #{onlineUserId}
WHERE id = #{id}

mapper.select:
SELECT id, zone_id, price, status, online_user_id 
FROM machines


        AND zone_id = #{zoneId}


        AND status = #{status}


        AND price >= #{minPrice}


        AND price <= #{maxPrice}

SELECT COUNT(*) 
FROM machines 
WHERE zone_id = #{zoneId}

SELECT COUNT(*) 
FROM machines 
WHERE status = #{status} 
AND zone_id = #{zoneId}

mapper.update:
UPDATE machines 
SET zone_id = NULL 
WHERE zone_id = #{zoneId}

mapper.select:
SELECT id, zone_id, price, status, online_user_id 
FROM machines 

    zone_id IS NULL

        AND status = #{status}


        AND price >= #{minPrice}


        AND price <= #{maxPrice}


ORDER BY id DESC
