mapper.select:
SELECT 
    id,
    order_date,
    total_price,
    status,
    machine_id,
    user_id,
    commodities
FROM orders


        AND JSON_SEARCH(commodities, 'one', #{commodityName}) IS NOT NULL


        AND order_date >= #{startDate}


        AND order_date <= #{endDate}


        AND status = #{status}


        AND user_id = #{userId}


        AND machine_id = #{machineId}


ORDER BY order_date DESC

mapper.update:
UPDATE orders 
SET status = #{status}
WHERE id = #{id}
