mapper.update:
UPDATE users
name = #{name}, identity_card = #{identityCard}, phone_number = #{phoneNumber}, balance = #{balance}, login_password = #{loginPassword}, status = #{status}, machine_id = #{machineId},
WHERE id = #{id}

mapper.select:
SELECT 
    id, name, identity_card, phone_number, balance, login_password,
    status, machine_id, last_on_computer_time, last_off_computer_time, register_time
FROM users


        AND (
            name LIKE CONCAT('%', #{keyword}, '%')
            OR identity_card LIKE CONCAT('%', #{keyword}, '%')
            OR phone_number LIKE CONCAT('%', #{keyword}, '%')
        )


        AND status = #{status}


        AND balance >= #{minBalance}


        AND balance <= #{maxBalance}


ORDER BY id DESC
